from bs4 import BeautifulSoup
import os,base64
import time

from aip import AipOcr

from helloWorld import print_info


class get_verify_code():

    image_file_absolute_path = ""

    def get_code_from_html(self, html):
        self.__parse_and_save_verify_code_image(html)
        return self.__read_number_in_image()

    def __parse_and_save_verify_code_image(self, html):
        soup = BeautifulSoup(html, features="lxml")
        if "网站访问认证页面" in soup.text and "请输入验证码后继续访问" in soup.text:
            img = soup.find("img",class_="verifyimg")
            imageBase64 = img.get("src")
            # print_info(imageBase64)
            imageBase64 = imageBase64.split(",")[1]

            time_stamp = int(time.time())
            file_path = os.path.split(os.path.realpath(__file__))[0] + r'/verify_code_images/' + str(time_stamp) + '.bmp'
                # str = ""
            imgdata = base64.b64decode(imageBase64)
            file = open(file_path, 'wb')
            file.write(imgdata)
            file.close()
            self.image_file_absolute_path = file_path
            return file_path

    def __read_number_in_image(self):
        # 识别图片中的数字验证码
        image = self.__get_file_content(self.image_file_absolute_path)

        """ 你的 APPID AK SK """
        APP_ID = '11460695'
        API_KEY = 'uZnBPApbqURDfO8mB1vtGOoX'
        SECRET_KEY = 'ksu26AhiugpwOwG5ARVYNHqcRQDP08BT'

        client = AipOcr(APP_ID, API_KEY, SECRET_KEY)
        # """ 调用通用文字识别, 图片参数为本地图片 """
        result = client.basicGeneral(image)
        return result.get("words_result")[0].get("words")


    def __get_file_content(self,filePath):
        """ 读取图片 """
        with open(filePath, 'rb') as fp:
            return fp.read()

